        private void xrPictureBox1_BeforePrint(object sender, System.Drawing.Printing.PrintEventArgs e)
        {
            var rowOrder = (AdminLteMvc.vwAttachments)GetCurrentRow();
            if (rowOrder != null && (rowOrder.ContentType == "image/jpeg" || rowOrder.ContentType == "image/png"))
            {
                var content = rowOrder.ByteContent;
                Models.FtdContext ftd = new Models.FtdContext();
                var vector = ftd.Attachments.Where(a => a.Id == rowOrder.Id).FirstOrDefault().Vector;


                using (Stream stream = new MemoryStream(Utilities.Utilities.Decrypt(content, vector), true))
                {
                    stream.Position = 0;
                    xrPictureBox1.Image = new Bitmap(stream);
                }
                // Get the total value.

            }
        }


       




